mapper.insert:


	SELECT case count(*) when 0 then 1 else max(bno) + 1 end bno FROM board_${room_code}

insert into board_${room_code} (bno, title, content, writer, notice)
values (#{bno}, #{title}, #{content}, #{writer}, #{notice})

mapper.select:

select *
FROM board_${room_code}
ORDER BY bno DESC
LIMIT #{skip}, #{amount}


